import React,{useState, useEffect} from 'react'
import { Form, Card, Container } from 'semantic-ui-react'
import CitieService from '../services/CitieService';
import JobPositionService from '../services/JobPositionService';

export default function JobAdvertisementAdd() {

    const [cities, setCities] = useState([]);
    const [jobPositions, setJobPositions] = useState([]);

    useEffect(() => {
        let citieService = new CitieService();
        citieService.getAllCities().then((result => setCities(result.data.data)));

        let jobPositionService = new JobPositionService();
        jobPositionService.getAllJobPosition().then((result => setJobPositions(result.data.data)));
    },[])


    const citieOptions = cities.map((citie) => ({
        key: citie.id,
        text: citie.cityName,
        value: citie.id,
    }));

    // const jobPositionOptions = jobPositions.map((jobPosition) => ({
    //     key: jobPosition.id,
    //     text: jobPosition.positionName,
    //     value: jobPosition.id,
    // }));

    return (
        <Container className="form">
            <Card fluid>
                <Card.Content header="İŞ İLANI YAYINLAMA"></Card.Content>
                <Card.Content>
                    <Form>
                        <Form.Group widths="equal">
                            <Form.Select
                                id="jobPositionId"
                                label="Pozisyon"
                                options={jobPositionOptions}
                                placeholder="Pozisyon Seçiniz"
                                search
                                selection
                            ></Form.Select>
                            <Form.Select
                                id="cityId"
                                options={citieOptions}
                                label="Şehir"
                                placeholder="Şehir Seçiniz"
                                search
                                selection
                            ></Form.Select>
                        </Form.Group>
                    </Form>
                </Card.Content>
            </Card>
        </Container>
    )
}
