import React from 'react'
import { ADD_TO_FAVORITE, REMOVE_TO_FAVORITE } from '../actions/favoriteActions';
import { favoriteItem } from "../initialValues/favoriteItems";

const initialState ={
    favoriteItem: favoriteItem
}

export default function favoriteReducer(state=initialState,{type,payload}) {
    switch (type) {
        case ADD_TO_FAVORITE:
            let favoriteItem = state.favoriteItem.find(f=>f.jobAdvertisement.id===payload.id)
            if(favoriteItem){
                return{
                    ...state
                }
            }else{
                return{
                    ...state,
                    favoriteItem:[...state.favoriteItem,{favoriteItem:payload}]
                }
            }
            
        case REMOVE_TO_FAVORITE:
            return{
                ...state,
                favoriteItem: state.favoriteItem.filter(f=>f.favoriteItem.id!==payload.id)
            }
    
        default:
            break;
    }
}
