import React,{ useState,useEffect } from 'react'
import { Form, Formik } from 'formik'
import * as Yup from 'yup';
import { Icon, Button, FormGroup, Select } from 'semantic-ui-react';
import KodlamaIoTextInput from '../utilities/customFormControls/KodlamaIoTextInput';
import WorkTimeService from '../services/WorkTimeService'

export default function JobAdvertisementAdd() {

    const [workTime, setWorkTime] = useState([])

    useEffect(() => {
        let workTimeService = new WorkTimeService();
        workTimeService.getAllWorkTime().then(result=>setWorkTime(result.data.data))

    }, [])

    const initialValues = {
        jobDescription: "",
        maxSalary: "",
        minSalary: 1,
        applicationDeadline: "",
        numberOfOpenPosition: 1,
    }

    const schema = Yup.object({
        jobDescription: Yup.string().required("Aciklama alani zorunludur"),
        maxSalary: Yup.number().required("Maximum maas alani zorunludur"),
        minSalary: Yup.number().required("Minimum maas alani zorunludur"),
        workTimeId: Yup.number().required("Lutfen calismaa zamani belirtiniz"),
        workTypeId: Yup.number().required("Lutfen calisma turu belirleyiniz")
    })

    const workTimeOptions = workTime.map(data=>({key:data.id,value:data.workTime,text:data.workTime}));

    return (
        <Formik
            initialValues={initialValues}
            validationSchema={schema}
            onSubmit={(values) => {
                values.employerId = 6
                console.log(values)
            }}
        >

            <Form className="ui form" style={{ marginTop: 100 }}>
                <FormGroup grouped>
                    <KodlamaIoTextInput name="maxSalary" placeholder="Maximum Salary"></KodlamaIoTextInput>
                    <KodlamaIoTextInput name="minSalary" placeholder="Minimum Salary"></KodlamaIoTextInput>
                </FormGroup>
                <FormGroup widths='equal'>
                    <KodlamaIoTextInput name="numberOfOpenPosition" placeholder=" Number of open states"></KodlamaIoTextInput>
                </FormGroup>
                <FormGroup grouped>
                    {
                        <select>
                            <option value={workTimeOptions.text}></option>
                        </select>
                    }
                    
                    <KodlamaIoTextInput name="workTimeId" placeholder="Calisma turu"></KodlamaIoTextInput>
                </FormGroup>
                <FormGroup widths='equal'>
                    <KodlamaIoTextInput name="jobDescription" placeholder="Aciklama"></KodlamaIoTextInput>
                </FormGroup>

                <Button color="blue" type="submit"> YAYINLA<Icon style={{ paddingLeft: 10 }} name="paper plane"></Icon></Button>
            </Form>


        </Formik>
    )
}
