import React from 'react'
import { Form, Formik, useFormik } from 'formik'
import { FormField } from 'semantic-ui-react'
import * as Yup from 'yup';

export default function JobAdvertisementAdd() {

    const initialValues= useFormik({
        cityId: "",
        jobPositionsId: "",
        maxSalary: "",
        minSalary: "",
        numberOfOpenPosition: 1,
        workTypeId: "",
        workTimeId: "",
        jobDescription: "",
        applicationDeadline: "",
    });

    const schema = Yup.object().shape({
        cityId: Yup.number().required("Sehir Bilgisi Alani Zorunludur"),
        jobPositionsId:Yup.number().required("Is Pozisyonu Bilgisi Alani Zorunludur"),
        minSalary: Yup.number().min(0,"Maas 0'dan Az Olamaz").required("Maximmum Maas Alani Zorunludur"),
        maxSalary: Yup.number().min(0,"Maas 0'dan Az Olamaz").required("Minimum Maas Alani Zorunludur"),
        numberOfOpenPosition: Yup.number().required("Lutfen Acik Pozisyon Sayisi Belirtiniz").min(1,"Acik Pozisyon 1'den Kucuk Olamaz"),
        workTypeId:Yup.number().required("Calisma Tipi Zorunludur"),
        workTimeId: Yup.number().required("Calisma Zamani Zorunludur"),
        jobDescription: Yup.string().required("Aciklama Alani Zorunludur"),
        applicationDeadline: Yup.date().required("Lutfen Son Basvuru Tarihini Belirtiniz")
    });

    return (
        <div className="ui">
            <Formik
            initialValues={initialValues}
            validationSchema={schema}
            onSubmit={(values)=>{
                // service kod
            }}
            >
            <Form className="ui form">
                <FormField></FormField>
            </Form>
            </Formik>
        </div>
    )
}
