import React, { useState, useEffect } from 'react'
import { Card, Button } from 'semantic-ui-react'
import JobAdvertisementService from '../services/JobAdvertisementService';

export default function JobAdvertisement() {

    const [jobAdvertisement, setJobAdvertisement] = useState([])

    useEffect(() => {
        let jobAdvertisementService = new JobAdvertisementService();
        jobAdvertisementService.getAllJobAdvertisement().then(result => setJobAdvertisement(result.data.data));

    }, [])

    return (

        <div>
            <Card.Group>
                <Card fluid>
                    {
                        jobAdvertisement.map(jobAdvertisement => (
                            <Card.Content>
                                <Card.Header floated='left'>Company Name</Card.Header>
                                <Card.Meta>Position Name</Card.Meta>
                                <Card.Meta>City Name</Card.Meta>
                                <Card.Description>
                                    Java , C# , Angular , React
                                </Card.Description>
                            </Card.Content>
                        ))
                    }

                    <Card.Content extra>
                        <div className='ui two buttons'>
                            <Button basic color='green'>
                                Incele
                            </Button>
                        </div>
                    </Card.Content>

                </Card>
            </Card.Group>
        </div >
    )
}
