import React from 'react'
import { Form, Formik } from 'formik'
import * as Yup from 'yup';
import { Icon, Button } from 'semantic-ui-react';
import KodlamaIoTextInput from '../utilities/customFormControls/KodlamaIoTextInput';

export default function JobAdvertisementAdd() {

    const initialValues = { jobDescription: "", maxSalary: "", minSalary: 1, applicationDeadline: "",numberOfOpenPosition: 1 }

    const schema = Yup.object({
        jobDescription: Yup.string().required("Aciklama alani zorunludur"),
        maxSalary: Yup.number().required("Maximum maas alani zorunludur"),
        minSalary: Yup.number().required("Minimum maas alani zorunludur"),
    })

    return (
        <Formik
            initialValues={initialValues}
            validationSchema={schema}
            onSubmit={(values) => {
                values.employerId=
                console.log(values)
            }}
        >

            <Form className="ui form" style={{marginTop:100}}>
                <KodlamaIoTextInput name="jobDescription" placeholder="Job Description"></KodlamaIoTextInput>
                <KodlamaIoTextInput name="maxSalary" placeholder="Maximum Salary"></KodlamaIoTextInput>
                <KodlamaIoTextInput name="minSalary" placeholder="Minimum Salary"></KodlamaIoTextInput>
                <Button color="blue" type="submit"> YAYINLA<Icon style={{paddingLeft:10}} name="paper plane"></Icon></Button>
            </Form>


        </Formik>
    )
}
